sys_script_include.script:
function InitializeRecord(strTable, record)
{
	var Debug = x_snc_devtools.Debug;
	Debug('DataSilos: InitializeRecord: '+strTable+' '+strSysId);

	strTable = ''.strTable;
	strSysId = ''.strSysId;
	if (strTable == '')
	{
		return;
	}
	/*
	var gr = new GlideRecord(strTable);
	if (!gr.get(strSysId))
	{
		Debug('SysId not found');
		return;
	}
	*/

	// Identify the user who created the record
	var strOpenedByUserSysId = record.opened_by;
	var grUser = GlideRecord('sys_user');
	if (grUser.get(strOpenedByUserSysId))
	{
		// Identify the user's groups
		var aUserGroups = [];

		var grUserGroups = new GlideRecord('sys_user_grmember');
		grUserGroups.addQuery('user',strOpenedByUserSysId);
		grUserGroups.query();
		while(gr.next())
		{
			aUserGroups.push(grUserGroups.group);
		}

		var aSilos = [];

		// Identify the user's silos based on its groups
		if (aGroups.length > 0)
		{
			var grSilos = new GlideRecord('x_snc_datasilos_datasilos');
			var query = grSilos.addQuery('x_snc_datasilos_group_whitelist','CONTAINS',aUserGroups[0]);
			for (var nIndex = 1; nIndex < aUserGroups.length; nIndex++)
			{
				query.addOrCondition('x_snc_datasilos_group_whitelist','CONTAINS',aUserGroups[nIndex]);
			}
			grSilos.query();
			while (grSilos.next())
			{
				aSilos.push(grSilos.sys_id);
			}
		}

		Debug(RenderValue(aSilos));
	}

	// 2. Apply the silo's groups to the record's white list


	// The old mechanism: add the current user's groups

	var aUserGroups = gs.getUser().getMyGroups();
	aUserGroups.forEach( function(strUserGroupSysId)
	{
		AddWhiteListGroup(record,strUserGroupSysId);
	});


}
