sys_script_include.script:
function GetConfigInstance()
{
	var config = GetConfigBaseline();

	config['active'] = gs.getProperty('x_snc_datasilos.active');

	// TODO: Iterate over tables
	var strValue = gs.getProperty('x_snc_datasilos.tables.incident.columns.instant_access.sys_user');
	if (strValue != '')
	{
		config['tables']['incident']['columns']['instant_access']['sys_user'] = strValue; 
	}

	// TODO: Support the whitelist_trigger columns

	var RenderValue = x_snc_devtools.RenderValue;
	Debug('GetConfigInstance: '+RenderValue(config));

	return config;
}
