sys_script_include.script:
function ExecuteRule(strTable, current, previous)
{
	var strOperation = '';
	strTable = current.getRecordClassName(); 
	//Debug('ExecuteRule: '+strTable);

	function Log(strText)
	{
		gs.info('DataSilos: ExecuteRule: '+strTable+': '+strOperation+': '+gs.getUserName()+': '+strText);
	}

	function IsActive(strTable)
	{
		var bActive = gs.getProperty('x_snc_datasilos.active');
		if (bActive == false)
			return false;
		if (IsTableSupported(strTable))
			return true;
		return false;
	}

	function GetOperation(current)
	{
		var strOperation = '';
		if (current != null)
		{
			strOperation = current.operation();
			if (strOperation == null)
			{
				strOperation = 'query';
			}
		}
		else
		{
			strOperation = 'undefined';
		}		
		return strOperation;
	}

	if (IsActive(strTable) == false)
	{
		return;
	}

	strOperation = GetOperation(current);

	Log('');

	if (strTable == 'incident')
	{
		if (strOperation == 'query')
		{
			RecordAddWhiteListQuery(strTable,current);
			return;
		}

		if (strOperation == 'insert')
		{
			RecordInitialize(strTable,current);
			return;
		}

		if (strOperation == 'update')
		{
			RecordUpdate(strTable,current);
			return;
		}
	}



	if (strTable == 'incident_task')
	{
		if (strOperation == 'query')
		{
			RecordAddWhiteListQuery(strTable,current);
			return;
		}

		if (strOperation == 'insert')
		{
			RecordInitialize(strTable,current);
			return;
		}

		if (strOperation == 'update')
		{
			RecordUpdate(strTable,current);
			return;
		}
	}




	if (strTable == 'change_request')
	{

		if (strOperation == 'query')
		{
			RecordAddWhiteListQuery(strTable,current);
			return;
		}

		if (strOperation == 'insert')
		{
			RecordInitialize(strTable,current);
			return;
		}

		if (strOperation == 'update')
		{
			RecordUpdate(strTable,current);
			return;
		}	
	}

	if (strTable == 'change_task')
	{
		if (strOperation == 'query')
		{
			RecordAddWhiteListQuery(strTable,current);
			return;
		}

		if (strOperation == 'insert')
		{
			RecordInitialize(strTable,current);
			return;
		}

		if (strOperation == 'update')
		{
			RecordUpdate(strTable,current);
			return;
		}
	}

}
