sys_script_include.script:
function GetRelatedRecords(record)
{
	if (record == null)
	{
		return [];
	}

	var strTable = record.getRecordClassName(); 

	if (strTable == 'incident')
	{
		var aRelatedRecords = [];

		var grIncidentTasks = new GlideRecord('incident_task');
		grIncidentTasks.addQuery('incident',record.getValue('sys_id'));
		grIncidentTasks.query();
		while (grIncidentTasks.next())
		{
			var strIncidentTaskSysId = grIncidentTasks.getValue('sys_id');
			var grIncidentTask = new GlideRecord('incident_task');
			if (grIncidentTask.get(strIncidentTaskSysId))
			{
				aRelatedRecords.push(grIncidentTask);
			}
		}
		return aRelatedRecords;
	}
	return [];
}
