sys_script_include.script:
function RecordGetWhitelistHash(record)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;

	var strRaw = '';

	if (IsValidRecord(record))
	{
		strRaw += 'GLOBAL'+GetStringValue(record.getValue('x_snc_datasilos_global'));
		var strGroupWhitelist = GetStringValue(record.getValue('x_snc_datasilos_group_whitelist'));
		strRaw += 'GROUP'+strGroupWhitelist;
		var strSupervisorWhitelist = GetStringValue(record.getValue('x_snc_datasilos_supervisor_whitelist'));
		strRaw += 'SUPERVISOR'+strSupervisorWhitelist;
	}

	var digest = new GlideDigest();
	return digest.getSHA256Hex(strRaw);
}
