sys_script_include.script:
var DataSilosClientAPI = Class.create();
DataSilosClientAPI.prototype = Object.extendsObject(global.AbstractAjaxProcessor,
{
	AccessAnalysisForRecordAndUser: function()
	{
		var GetStringValue = x_snc_devtools.GetStringValue;
		var IsValidRecord = x_snc_devtools.IsValidRecord;
		var GetRecord = x_snc_devtools.GetRecord;

		var strUser = GetStringValue(this.getParameter('sysparm_user'));
		var strTable = GetStringValue(this.getParameter('sysparm_table'));
		var strRecord = GetStringValue(this.getParameter('sysparm_record'));
		//Debug('DataSilosClientAPI: AccessAnalysisForRecordAndUser: user='+strUser);
		//return GetTableFromSysId(strSysId);

		var grUser = GetRecord('sys_user',strUser);
		if (IsValidRecord(grUser) == false)
		{
			grUser = new GlideRecord('sys_user');
			grUser.addQuery('user_name','=',strUser);
			grUser.query();
			if (grUser.getRowCount() != 1)
			{
				return 'Cannot identfy user based on "'+strUser+'"';
			}
			grUser.next();
		}

		var records = {};
		records[strTable] = [strRecord];

		var result = AccessAnalysis([GetStringValue(grUser.sys_id)],records);
		//Debug('DataSilosClientAPI: AccessAnalysisForRecordAndUser: '+x_snc_devtools.RenderValue(result));

		var strResult = GetStringValue(result['users'][0]['tables'][strTable][0].explanation);

		if (strResult == '')
		{
			strResult = 'Error: Something went wrong.';
		}

		//Debug('DataSilosClientAPI: AnalyseAccessToRecordForUser: '+strResult);

		return strResult;
	},

    type: 'DataSilosClientAPI'
});
