sys_script_include.script:
function RecordUpdate(strTable, record)
{
	var Debug = x_snc_devtools.Debug;
	var RenderValue = x_snc_devtools.RenderValue;
	var ArrayUnique = x_snc_devtools.ArrayUnique;
	var GetStringValue = x_snc_devtools.GetStringValue;

	strTable = GetStringValue(strTable);

	Debug('DataSilos: RecordUpdate: '+strTable+': '+RenderValue(record));


	if (strTable == '')
	{
		gs.info('DataSilos: RecordUpdate: table not specified');
		return false;
	}
	if (record == undefined)
	{
		gs.info('DataSilos: RecordUpdate: record is undefined');
		return false;
	}

	if (IsTableSupported(strTable))
	{
		// 
		var strAssignmentGroup = record.assignment_group.toString();

		// Add current assignment group
		RecordAddGroupToWhiteList(record,strAssignmentGroup);

		Debug('DataSilos: RecordUpdate: '+strTable+': after RecordAddGroupToWhiteList: '+RenderValue(record));

		// Add the corresponding management group(s)
		var aSilos = GetSilosFromGroup(strAssignmentGroup);

		Debug('DataSilos: RecordUpdate: '+strTable+': GetSilosFromGroup: '+RenderValue(aSilos));

		var aGroupsToAdd = [];
		if (aSilos.length > 0)
		{
			var grSilosToGroups = new GlideRecord('x_snc_datasilos_m2m_groups_datasilos');
			grSilosToGroups.addQuery('type','=','supervisor');
			var query = grSilosToGroups.addQuery('datasilo','=',aSilos[0]);
			for (var nIndexSilo = 1; nIndexSilo < aSilos.length; nIndexSilo++) 
			{
				query.addOrCondition('datasilo','=',aSilos[nIndexSilo]);
			}
			grSilosToGroups.query();
			while (grSilosToGroups.next())
			{
				aGroupsToAdd.push(grSilosToGroups.getValue('group'));
			}
		}

		Debug('DataSilos: RecordUpdate: '+strTable+': Supervisor groups: '+RenderValue(aGroupsToAdd));

		// Apply the supervisor groups to the record's white list
		for (var nIndexGroup = 0; nIndexGroup < aGroupsToAdd.length; nIndexGroup++)
		{
			RecordAddGroupToWhiteList(record,aGroupsToAdd[nIndexGroup]);
		}

	}

	if (IsTableRelatedRecord(strTable))
	{
		if (strTable == 'incident_task')
		{
			// Propagate groups to parent ticket
			var strParentIncidentSysId = current.incident;
			//Log('strParentIncidentSysId: '+strParentIncidentSysId);
			var grParentIncident = new GlideRecord('incident');
			if (grParentIncident.get(strParentIncidentSysId ))
			{
				var strWhiteListGroupSysIds = current.x_snc_datasilos_group_whitelist;
				//Log('strWhiteListGroupSysIds: '+strWhiteListGroupSysIds);
				var aWhiteListGroups = strWhiteListGroupSysIds.split(',');
				aWhiteListGroups.forEach( function(strWhiteListGroupSysId)
				{
					RecordAddGroupToWhiteList(grParentIncident,strWhiteListGroupSysId);
				});
				grParentIncident.update();
			}			
		}
	}

	return false;
}
